// eslint-disable-next-line no-unused-vars
import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

const WorkInfo = ({ number, title }) => (
  <div className="work_info h-[100vh] flex flex-col justify-center">
    <div className="work_left-bl">
      <span className="work_num block text-right font-lg skew-x-3">{number}</span>
      <h2 className="title text-2xl uppercase">{title}</h2>
    </div>
  </div>
);

function About() {
  const workPhotoItemsRef = useRef([]);

  useEffect(() => {
    gsap.set(workPhotoItemsRef.current, {
      clipPath: "inset(0px 0px 100% 0px)"
    });

    if (workPhotoItemsRef.current[0]) {
        gsap.set(workPhotoItemsRef.current[0], {
          clipPath: "inset(0px 0px 0px 0px)"
        });
      }

    const animation = gsap.to(workPhotoItemsRef.current.slice(1), {
      clipPath: "inset(0px 0px 0px 0px)",
      stagger: 0.5,
      ease: "none"
    });

    ScrollTrigger.create({
      trigger: ".work",
      start: "top top",
      end: "bottom bottom",
      animation: animation,
      scrub: 1
    });
  }, []);

  const workItems = [
    { number: "01", title: "Image animated", src: "data:image/jpeg;base64," },
    { number: "02", title: "Image animated", src: "data:image/jpeg;base64," },
    { number: "03", title: "Image animated", src: "data:image/jpeg;base64," },
    { number: "04", title: "Image animated", src: "data:image/jpeg;base64," },
  ];

  return (
    <div className="wrapp relative w-full px-[20px] z-1">
      <div className="spacer w-full h-[55vh] bg-[#ddd]"></div>
      <section className="work flex">
        <div className="work_left relative w-[54%] z-2 ">
          <div className="work_text w-[80%]">
            {workItems.map((item, index) => (
              <WorkInfo key={index} number={item.number} title={item.title} />
            ))}
          </div>
        </div>
        <div className="work_right w-[46%]">
          <div className="work_right-bl flex flex-col justify-center sticky w-[100%] h-[100vh] top-0">
            <div className="work_photo w-[40vw] h-[30vw] relative">
              {workItems.map((item, index) => (
                <div
                  key={index}
                  ref={(el) => (workPhotoItemsRef.current[index] = el)}
                  className="work_photo-item absolute w-[100%] h-[100%] cursor-pointer rounded-lg overflow-hidden"
                  title={item.number}
                >
                  <img src={item.src} alt="" className="block w-[100%] h-[100%] object-cover" />
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>
      <div className="spacer"></div>
    </div>
  );
}

export default About;
